import React from "react";
import "./Home.css";
function Home(props) {


  return (
    <>
      <section>
        <div>
         
        <div className="content my-5">
          <span className="fw-500 text-info">Hi, my name is </span>
          <div
            style={{ fontWeight: "bold", fontSize: "5rem" }}
            className={`name text-${props.mode==='light'?"dark":"light"}`}
          >
            Md Shoyeb
          </div>
          <div
            style={{ fontWeight: "bold", fontSize: "3rem" }}
            className={`name text-${props.mode==='light'?"dark":"light"}`}
          >
            Make your business shine on the web
          </div>
          <div className={`message mt-5  col-12 col-lg-8 text-${props.mode==='light'?"dark":"light"}`}>
            I am a full stack web developer and software engineer creates best
            design and responsive website with backend.
            <div className="d-flex  justify-content-between mt-5">
              {" "}
              <a href='contact'
             spy={true}
              smooth={true}
              offset={50}
              duration={500}
                type="button"
                className="btn border border-info  px-5 py-2 btn-outline-info"
              >
                Hire Me
              </a>
              <a href="./SHOYEB_RESUME_updated.pdf" download="Resume" className="btn border border-info btn-outline-info px-5 py-2 "
              >
                Resume
              </a>
           
            </div>
          </div>
        </div>
        </div>
      </section>
    </>
  );
}

export default Home;
