import "./App.css";
import Connect from "./component/connect/Connect";
import "../node_modules/bootstrap/dist/css/bootstrap.min.css";
import "../node_modules/bootstrap/dist/js/bootstrap.bundle";
import { useState } from "react";

function App() {
  const [mode,setmode]=useState("light");
  const togglemode=()=>{
    if(mode==="light"){
      setmode("dark");
      document.body.style.backgroundColor="black";
    }else{
      setmode("light");
      document.body.style.backgroundColor="white";

    }
  }
  return (
    <div className="App" id="app">
      <div className="container-fluid">
        <div className="row">
          <div className="col-9 col-lg-10  mx-auto ">
            <Connect mode={mode} togglemode={togglemode} />
          </div>
        </div>
      </div>
      <div style={{position:"fixed" ,top:"14rem" ,}}  className="d-flex flex-column  w-5 sidbar">
      <span className="mb-5" data-bs-toggle="tooltip" data-bs-placement="top" title="Facebook"><a blank href="https://www.facebook.com/md.shoyeb.98096/"> <i className={`uil text-${mode==='light'?"dark":"light"} fs-2 px-4  uil-facebook`}></i> </a></span>
      <span className="mb-5" data-bs-toggle="tooltip" data-bs-placement="top" title="Linkedin"> <a blank href="https://www.linkedin.com/in/md-shoyeb-49645020a">  <i className={`uil text-${mode==='light'?"dark":"light"} fs-2 px-4 uil-linkedin`}></i></a></span>
      <span className="mb-5" data-bs-toggle="tooltip" data-bs-placement="top" title="Github"> <a blank href="https://github.com/shoyebmd424"> <i className={`uil text-${mode==='light'?"dark":"light"} fs-2 px-4 uil-github`}></i></a></span>
      <span className="mb-5" data-bs-toggle="tooltip" data-bs-placement="top" title="whatsapp">   <a blank href="https://wa.me/919871904481"> <i className={`uil text-${mode==='light'?"dark":"light"} fs-2 px-4 uil-whatsapp`}></i> </a></span>
      <div className={`bg-${mode==='light'?"dark":"light"} sideemp`}></div>
        </div>
        <div className="right">
          <a className={`text-${mode==='light'?"dark":"light"}`} href="mailto:shoyebmd424@gmail.com">shoyebmd424@gmail.com</a>
          <div className={`emp bg-${mode==='light'?"dark":"light"}`}></div>
        </div>
    </div>
  );
}

export default App;
