import React from "react";
import { Data } from "../data/Data";
function Project(props) {
  return (
    <div className="projects mb-5" id="project">
      <div className="gym">
        <h1
          style={{ fontSize: "3rem", textTransform: "uppercase" }}
          className=" stroke-text mt-5"
        >
          <i> Some Projects i've built </i>
        </h1>
        <div className=" mt-4 project">
          <div className="pro">
            <div className="container-fluid">
              <div className="row ">
                  {Data.map((data)=>(
                <div style={{flexWrap:"wrap"}} className="col-12 mt-5 d-flex  ">
          
                 <div
                 className={`card col-12 col-lg-6 shadow bg-${
                   props.mode === "light" ? "dark" : "light"
                  }`}
                  >
                  <h1 className={`text-center text-${props.mode}`}>{data.name}</h1>
                  <video autoPlay loop muted>
                  {data.video}
                  </video>
                </div>

                <div className="ms-lg-5 mt-4 col-12 col-lg-5 video-rside ">
                  <p
                    className={`text-${
                      props.mode === "light" ? "dark" : "light"
                    }`}
                  >
                    Project Featured
                  </p>
                  <h3 className="text-info">{data.name}</h3>
                  <div
                    className={`shadow bg-${
                      props.mode === "light" ? "dark" : "light"
                    } p-4 mt-4 `}
                    style={{ borderRadius: "1rem" }}
                  >
                    <p className={` text-${props.mode}`}>
                     {data.desc}
                      <a
                        style={{ textDecoration: "none" }}
                        className="text-info"
                        href="https://shoyebmd424.github.io/sbgym/"
                        >
                        {" "}
                        github{" "}
                      </a>
                      .
                    </p>
                  </div>
                  <div style={{flexWrap:"wrap"}} className="d-flex">
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      React js
                    </li>
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      {" "}
                      Html
                    </li>
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      Bootstrap
                    </li>
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      Css
                    </li>
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      PHP
                    </li>
                    <li
                      className={`text-${
                        props.mode === "light" ? "dark" : "light"
                      }`}
                      style={{ listStyle: "none", padding: "1rem" }}
                      >
                      MYSQL
                    </li>
                    </div>
                  </div>
                </div>
                  ))}
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Project;
