import React,{useState,useRef} from "react";
import "./Contact.css"
import emailjs from '@emailjs/browser';

export default function Contact() {

 const form = useRef();
  const [name,setname] =useState("");
  const [number,setnumber] =useState("");
  const [email,setemail] =useState("");
  const [subject,setsubject] =useState("");
  const [textarea,settextarea] =useState("");
  const ename=(event)=>{ setname(event.target.value); }
  const enumber=(event)=>{ setnumber(event.target.value); }
  const eemail=(event)=>{ setemail(event.target.value); }
  const esubject=(event)=>{ setsubject(event.target.value); }
  const etextarea=(event)=>{ settextarea(event.target.value); }
  const sendEmail = (e) => {
      e.preventDefault();
      emailjs.sendForm("service_g0ljg36","template_bbsfoii", form.current, "wm03_iaKbW0nr-302")
        .then((result) => {
            console.log(result.text);
        }, (error) => {
            console.log(error.text);
        });
        setnumber('');
        settextarea('');
        setname('');
        setemail("");
        setsubject("");
        alert("You data is submmited succesfully");

    };
    return (
    <>
        <section className="contact" id="contact">
          <div className="main">
            <h3>Contact Us</h3>
            </div>
          <div className="Detail">
            <div className="left">
           <p>Nasim Alam</p>
           <p>React Js Devloper</p>
           <p>I am available for work. Connect with me via and call in to my account.</p>
           <p><i>BsFillTelephoneFill</i><a href="tel:+917654182972">+917654182972</a></p>
           <p> E-Mail:-<a href="mailto:alamnasim492@gmail.com">alamnasim492@gmail.com</a></p>
           </div>
           <div className="right">
            <form>
              <input type="text"placeholder="Full Name" className="name"/><br/>
              <input type="text"placeholder="Email" />
              <input type="text"placeholder="Phone Number"/><br/>
              <input type="text"placeholder=""id="text-area" /><br/>
              <button type="sumbit" id="btn">Sumbit</button>
            </form>
          </div>
          </div>
          
    </section>
        
    </>
  )
}
